/*!

=========================================================
* Black Dashboard React v1.2.1
=========================================================

* Product Page: https://www.creative-tim.com/product/black-dashboard-react
* Copyright 2022 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/black-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from 'react';

import { Dropdown, DropdownToggle, Badge } from 'reactstrap';
import { ThemeContext, themes } from 'contexts/ThemeContext';
import { backgroundColors } from 'contexts/BackgroundColorContext';

function FixedPlugin(props) {
  const [dropDownIsOpen, setdropDownIsOpen] = React.useState(false);
  const handleClick = () => {
    setdropDownIsOpen(!dropDownIsOpen);
  };
  return (
    <div className="fixed-plugin">
      <Dropdown isOpen={dropDownIsOpen} toggle={handleClick}>
        <DropdownToggle tag="div">
          <i className="fa fa-cog fa-2x" />
        </DropdownToggle>
        <ul className="dropdown-menu show">
          <li className="header-title">SIDEBAR BACKGROUND</li>
          <li className="adjustments-line">
            <div className="badge-colors text-center">
              <Badge
                color="primary"
                className={props.bgColor === backgroundColors.primary ? 'active' : ''}
                onClick={() => {
                  props.handleBgClick(backgroundColors.primary);
                }}
              />{' '}
              <Badge
                color="info"
                className={props.bgColor === backgroundColors.blue ? 'active' : ''}
                onClick={() => {
                  props.handleBgClick(backgroundColors.blue);
                }}
              />{' '}
              <Badge
                color="success"
                className={props.bgColor === backgroundColors.green ? 'active' : ''}
                onClick={() => {
                  props.handleBgClick(backgroundColors.green);
                }}
              />{' '}
            </div>
          </li>
          <li className="adjustments-line text-center color-change">
            <ThemeContext.Consumer>
              {({ changeTheme }) => (
                <>
                  <span className="color-label">LIGHT MODE</span>{' '}
                  <Badge className="light-badge mr-2" onClick={() => changeTheme(themes.light)} />{' '}
                  <Badge className="dark-badge ml-2" onClick={() => changeTheme(themes.dark)} />{' '}
                  <span className="color-label">DARK MODE</span>{' '}
                </>
              )}
            </ThemeContext.Consumer>
          </li>
        </ul>
      </Dropdown>
    </div>
  );
}

export default FixedPlugin;
